import React, { useState } from 'react';
import styled from 'styled-components';
import Comments from '../components/Comments';
import CommentInput from '../components/CommentInput';
import Carousel from '../components/Carousel';
import { ConstructionOutlined, NoEncryption } from '@mui/icons-material';

const ImagePageWrap = styled.div`
  display: flex;
  margin: 50px 50px;
`;

const CarouselWrap = styled.div`
  width: 60%;
`;

const CommentsWrap = styled.div`
  display: flex;
  flex-direction: column;
  margin-left: 50px;
  width: 40%;
  align-items: flex-start;
`;
const Submit = styled.input``;

const CommentWrap = styled.div`
  display: flex;
  align-items: flex-end;
`;

const TextInput = styled.textarea`
  margin-top: 10px;
  font-size: 20px;
  width: 80%;
  height: 100px;
`;

function Return({ index, id, comment }) {
  if (index === 1) {
    return (
      <Comments
        id={id}
        comment={comment}
        src="data:image/jpeg;base64,"
      />
    );
  } else {
    return <div></div>;
  }
}

export default function ImagePage() {
  const [id, setId] = useState('kjs');
  const [index, setIndex] = useState(0);
  const [comment, setComment] = useState('hi');

  const SLIDE_COUNT = 10;
  const slides = Array.from(Array(SLIDE_COUNT).keys());
  const handleClick = () => {
    setIndex(1);
  };

  const handleChange = (e) => {
    setComment(e.target.value);
  };
  return (
    <ImagePageWrap>
      <CarouselWrap>
        <Carousel slides={slides} />
      </CarouselWrap>

      <CommentsWrap>
        <h1>Comments</h1>
        {/* 댓글 목록 */}
        <Comments
          id="psy"
          comment="hello!"
          src="data:image/jpeg;base64,"
        ></Comments>
        <Comments
          id="kjs"
          comment="hello!"
          src="https://mblogthumb-phinf.pstatic.net/MjAyMDAzMTNfMjA2/MDAxNTg0MDI3MzA1MTM1.ziQdHXjoSVpswgl7MkBXPOamMHIiKQKPpqjtQNkw6IMg.rkebY82ViYlYz83X1y5B7Fm6qyQkC2ZZlmHgRyJw1vAg.JPEG.d_hye97/1.jpg?type=w800"
        ></Comments>
        <Return index={index} id={id} comment={comment} />
        {/* 댓글 입력창 */}
        <div
          style={{
            display: 'flex',
            flexDirection: 'column-reverse',
            position: 'absolute',
            top: '500px',
          }}
        >
          <CommentWrap>
            <form>
              <input
                type="text"
                onChange={handleChange}
                style={{ height: 100, width: 400, margin: 10 }}
              />
              <input
                type="submit"
                value="Enter"
                onClick={handleClick}
                style={{ fontSize: 20 }}
              />
            </form>
            {/* <CommentInput id="kjs" src=""></CommentInput> */}
            {/* <TextInput rows="5"></TextInput>
            <Submit type="submit" value="Submit" onClick={submit}></Submit> */}
          </CommentWrap>
        </div>
      </CommentsWrap>
    </ImagePageWrap>
  );
}
